mode_state_template:
{% if value_json.operationStatus == 'false' %}
  off
{% else %}
  {% set mapping = {'auto':'auto','cooling':'cool','heating':'heat','dehumidification':'dry','circulation':'fan_only'} %}
  {{ mapping.get(value_json.operationMode) }}
{% endif %}

mode_command_template:
{% if value == 'off' %}
  {"operationStatus": "false"}
{% else %}
  {% set mapping = {'auto':'auto','cool':'cooling','heat':'heating','dry':'dehumidification','fan_only':'circulation'} %}
  {"operationStatus": "true", "operationMode": "{{ mapping[value] }}"}
{% endif %}

swing_mode_state_template:
{% if value_json.automaticControlAirFlowDirection == 'autoVertical' %}
  自動
{% else %}
  {% set mapping = {'uppermost':'上','lowermost':'下','central':'中央','upperCenter':'上中','lowerCenter':'下中'} %}
  {{ mapping.get(value_json.airFlowDirectionVertical) }}
{% endif %}

swing_mode_command_template:
{% if value == '自動' %}
    {"automaticControlAirFlowDirection": "autoVertical"}
{% else %}
  {% set mapping = {'上':'uppermost','下':'lowermost','中央':'central','上中':'upperCenter','下中':'lowerCenter'} %}
    {"airFlowDirectionVertical": "{{ mapping[value] }}"}
{% endif %}

temperature_state_template:
{% if value == 'undefined' %}
  None
{% else %}
  {{ value }}
{% endif %}
